function getTradeCost(

       uint256 inputMarketId,

       uint256 outputMarketId,

       Account.Info memory makerAccount,

       Account.Info memory /* takerAccount */,

       Types.Par memory oldInputPar,

       Types.Par memory newInputPar,

       Types.Wei memory inputWei,

       bytes memory data

   )

   public

   onlySolo(msg.sender)

   returns (Types.AssetAmount memory)

   {

       // return zero if input amount is zero

       if (inputWei.isZero()) {

           return Types.AssetAmount({

           sign : true,

           denomination : Types.AssetDenomination.Par,

           ref : Types.AssetReference.Delta,

           value : 0

           });

       }


       (

       uint256 owedMarketId,

       uint32 maxExpiry

       ) = parseTradeArgs(data);


       uint32 expiry = getExpiry(makerAccount, owedMarketId);


       // validate expiry

       Require.that(

           expiry != 0,

           FILE,

           "Expiry not set",

           makerAccount.owner,

           makerAccount.number,

           owedMarketId

       );

       Require.that(

           expiry <= Time.currentTime(),

           FILE,

           "Borrow not yet expired",

           expiry

       );

       Require.that(

           expiry <= maxExpiry,

           FILE,

           "Expiry past maxExpiry",

           expiry

       );


       return getTradeCostInternal(

           inputMarketId,

           outputMarketId,

           makerAccount,

           oldInputPar,

           newInputPar,

           inputWei,

           owedMarketId,

           expiry

       );

