results.before:
function executable(
        bool shouldSucceed,
        bytes memory data,
        bytes memory returnData
    )
    public
    returns (bytes memory)
    {
        emit ExecutableCalled(
            data,
            returnData,
            currentContextAddress
        );
        require(shouldSucceed, "EXECUTABLE_FAILED");
        if (data.length != 0) {
            (bool didSucceed, bytes memory callResultData) = address(this).delegatecall(data);
            // This is a delegatecall to preserve the `msg.sender` field
            if (!didSucceed) {
                assembly {revert(add(callResultData, 0x20), mload(callResultData))}
            }
        }
        return returnData;
    }

